import React, { useState } from 'react';
// import './List.css';

function List({ data }) {
  const [showInfo, setShowInfo] = useState({});

  const toggleInfo = (itemId) => {
    setShowInfo((prevShowInfo) => ({
      ...prevShowInfo,
      [itemId]: !prevShowInfo[itemId],
    }));
  };

  return (
    <div className="List">
      {data.map((item) => (
        <div key={item.id} className="list-item">
          <div className="item-header">
            <span>{item.title}</span>
            <button onClick={() => toggleInfo(item.id)}>
              {showInfo[item.id] ? 'Hide Info' : 'Show Info'}
            </button>
          </div>
          {showInfo[item.id] && <p className="item-info">{item.description}</p>}
        </div>
      ))}
    </div>
  );
}

export default List;
