import React, { useState } from 'react';
import "./Page.css";

function Page({ id, img, about1, about2, section, info, logo }) {

    const [switchSection, setSwitch] = useState(false);

    const handleSwitch = () => {
        setSwitch(!switchsection);
    }

  return (
    <div className="Page" id = {id}>

        {about1 ? (
        <div className = "mainpage">
            <div className = "img-container">
                <img src = {img} alt ="" className = "pic"/>
            </div>
            <div id = "mainintro">
                <h2 id = "main"> hello! I'm Krystal Pothilat</h2>
                <ul className = "text" id = "cookiepoints">
                    <li> San Diego, CA</li>
                    <li> B.S. in Computer Science at UC Riverside</li>
                </ul>
            </div>
        </div>  
        ) : about2 ? (
            <div className = "aboutpage"> 
                <div className = "header-container">
                    <img src = {img} alt = "" className = "header-banner"/>
                    <p className = "header"> {section} </p>
                </div>
                <div className = "about">
                    {Array.isArray(info) && info.map((item) => (

                        <div className = {`about-sec ${switchSection ? 'switchSection' : ''}`} 
                        onMouseEnter={handleSwitch}
                        onMouseLeave={handleSwitch}>
                            <img src = {frontpic} alt = "" className = "pic"/>
                            <h2 className = "label">{item.title}</h2>

                        </div>
                    ))}
                </div>

            </div>

        ) : (
            <div className = "infopage">
                <div className = "header-container">
                    <img src = {img} alt = "" className = "header-banner"/>
                    <p className = "header"> {section} </p>
                    {logo && <img src={logo} alt="Logo" className="logo" />}

                </div>
                        <div className = "info">
                    {Array.isArray(info) && info.map((item) => (
                        <div className = "info-container">
                            <h2 id = "title"> {item.title}</h2>
                            <p className = "text"> {item.description} </p>
                            {item.bulletpoints && (
                            <ul className = "text">
                                {item.bulletpoints.map((bulletpoints, index) => (
                                    <li key={index}>{bulletpoints}</li>
                                ))}
                            </ul>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        )}


    </div>
  );
}

export default Page;
