import React, { useState } from "react";
import "./InfoSection.css";
import externtab from "./imgs/externtab.png";

function InfoSection({ id, title, date, bulletpoints, description, skills, link, coverimg }) {
    const [isHovered, setIsHovered] = useState(false);

    return (
        <div>
            <div className="info">
                <h2 className="title">{title}</h2>
                <div className="hidden-section">
                    {coverimg && !isHovered && (
                        <img
                            src={coverimg}
                            alt=""
                            className="cover-image"
                            onMouseEnter={() => setIsHovered(true)}
                            onMouseLeave={() => setIsHovered(false)}
                        />
                    )}
                    {!coverimg || isHovered ? (
                        <>
                            <h3 className="date">{date}</h3>
                            <p className="text">{description}</p>
                            {bulletpoints && (
                                <ul className="text" id="info-bulletpoints">
                                    {bulletpoints.map((bullet, index) => (
                                        <li key={index}>{bullet}</li>
                                    ))}
                                </ul>
                            )}
                            {skills && (
                                <div className="skills-container">
                                    {skills.map((skill, index) => (
                                        <p key={index} className="text" id="skill">{skill}</p>
                                    ))}
                                </div>
                            )}
                        </> 
                    ) : null }
                </div>
            </div>
        </div>
    );
}

export default InfoSection;
